import React from 'react';
import {api} from "../utils/api";

let userID;
export default function Main(props) {

    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');
    const [cards, setCards] = React.useState([])

    React.useEffect(() => {
        Promise.all([
            //в Promise.all передаем массив промисов которые нужно выполнить
            api.getUserInfo(),
            api.getInitialCards()
        ])
            .then((values) => {
                //попадаем сюда когда оба промиса будут выполнены
                const [userData, initialCards] = values;
                console.log(cards);
                userID = userData._id;
                setUserName(userData.name);
                setUserDescription(userData.about);
                setUserAvatar(userData.avatar);
                setCards([...cards, initialCards]);
                // у нас есть все нужные данные, отрисовываем страницу
            })
            .catch((err) => {
                //попадаем сюда если один из промисов завершаться ошибкой
                console.log(err);
            });
    })

    return (
        <main className="content">
            <section className="profile">
                <div className="profile__description">
                    <div className="profile__avatar">
                        <img
                            className="profile__picture"
                            src={userAvatar}
                            alt="Фотография Жака-Ив Кусто"
                        />
                        <button className="button botton_type_avatar" type="button"
                                onClick={props.onEditAvatar}></button>
                    </div>
                    <div className="profile__text">
                        <div className="profile__wrapper">
                            <h1 className="profile__title">{userName}</h1>
                            <button className="button button_type_edit" type="button"
                                    onClick={props.onEditProfile}></button>
                        </div>
                        <p className="profile__subtitle">{userDescription}</p>
                    </div>
                </div>
                <button className="button button_type_add" type="button" onClick={props.onAddPlace}></button>
            </section>
            <section className="photo-grid">
                <ul className="photo-grid__list">
                    <li key={i} className="card__element">
                        <div className="card">
                            <button className="button button_type_delete" type="button"></button>
                            <img
                                className="photo-grid__item"
                                src="#"
                                alt="Картинка, которая иллюстрирует название"
                            />
                            <div className="photo-grid__caption">
                                <h2 className="photo-grid__title">#</h2>
                                <div className="photo-grid__wrapper">
                                    <button className="button button_type_like" type="button"></button>
                                    <span className="photo-grid__like-counter">0</span>
                                </div>
                            </div>
                        </div>
                    </li>
                </ul>
            </section>
        </main>
    )
}
