import React from 'react';
import {api} from "../utils/api";

let userID;
export default function Main(props) {

    const [userName, setUserName] = React.useState('gg');
    const [userDescription, setuserDescription] = React.useState('gg');
    const [userAvatar, setuserAvatar] = React.useState('gg');

    React.useEffect(() => {
        Promise.all([
            //в Promise.all передаем массив промисов которые нужно выполнить
            api.getUserInfo(),
        ])
            .then((values) => {
                //попадаем сюда когда оба промиса будут выполнены
                const [userData] = values;
                console.log(values);
                userID = userData._id;
                setUserName(userData.name);
                setuserDescription(userData.about);
                setuserAvatar(userData.avatar)
                // у нас есть все нужные данные, отрисовываем страницу
            })
            .catch((err) => {
                //попадаем сюда если один из промисов завершаться ошибкой
                console.log(err);
            });
    })

    return (
        <main className="content">
            <section className="profile">
                <div className="profile__description">
                    <div className="profile__avatar">
                        <img
                            className="profile__picture"
                            src={userAvatar}
                            alt="Фотография Жака-Ив Кусто"
                        />
                        <button className="button botton_type_avatar" type="button"
                                onClick={props.onEditAvatar}></button>
                    </div>
                    <div className="profile__text">
                        <div className="profile__wrapper">
                            <h1 className="profile__title">{userName}</h1>
                            <button className="button button_type_edit" type="button"
                                    onClick={props.onEditProfile}></button>
                        </div>
                        <p className="profile__subtitle">{userDescription}</p>
                    </div>
                </div>
                <button className="button button_type_add" type="button" onClick={props.onAddPlace}></button>
            </section>
            <section className="photo-grid">
                <ul className="photo-grid__list"></ul>
            </section>
        </main>
    )
}
