export function ImagePopup(props) {
    return (
        <div className={`popup popup_type_fig ${!!(props.card) ? 'popup_opened' : ''}`}>
            <figure className="popup__figure">
                <button className="button button_type_close" type="button" onClick={props.onClose}></button>
                <img
                    className="popup__img"
                    src={props.card ? props.card.link : ''}
                    alt="Картинка, которая иллюстрирует название"
                />
                <figcaption className="popup__figcaption popup__figcaption-text">{props.card ? props.card.name : ''}</figcaption>
                <figcaption className="popup__figcaption popup__figcaption-owner">{props.card ? props.card._owner : ''}</figcaption>
                <figcaption className="popup__figcaption popup__figcaption-date">{props.card ? props.card.date : ''}</figcaption>
            </figure>
        </div>
    );
}
